// invalidate characters after EOS IF a[m+size-1:m] == 0 aInvalid := 1 FI IF b[n+size-1:n] == 0 bInvalid := 1 FI
// override comparisons for invalid characters CASE (imm8[3:2]) OF 0: // equal any IF (!aInvalid && bInvalid) BoolRes.word[i].bit[j] := 0 ELSE IF (aInvalid && !bInvalid) BoolRes.word[i].bit[j] := 0 ELSE IF (aInvalid && bInvalid) BoolRes.word[i].bit[j] := 0 FI 1: // ranges IF (!aInvalid && bInvalid) BoolRes.word[i].bit[j] := 0 ELSE IF (aInvalid && !bInvalid) BoolRes.word[i].bit[j] := 0 ELSE IF (aInvalid && bInvalid) BoolRes.word[i].bit[j] := 0 FI 2: // equal each IF (!aInvalid && bInvalid) BoolRes.word[i].bit[j] := 0 ELSE IF (aInvalid && !bInvalid) BoolRes.word[i].bit[j] := 0 ELSE IF (aInvalid && bInvalid) BoolRes.word[i].bit[j] := 1 FI 3: // equal ordered IF (!aInvalid && bInvalid) BoolRes.word[i].bit[j] := 0 ELSE IF (aInvalid && !bInvalid) BoolRes.word[i].bit[j] := 1 ELSE IF (aInvalid && bInvalid) BoolRes.word[i].bit[j] := 1 FI ESAC ENDFOR ENDFOR // aggregate results CASE (imm8[3:2]) OF 0: // equal any IntRes1 := 0 FOR i := 0 to UpperBound FOR j := 0 to UpperBound IntRes1[i] := IntRes1[i] OR BoolRes.word[i].bit[j] ENDFOR ENDFOR 1: // ranges IntRes1 := 0 FOR i := 0 to UpperBound FOR j := 0 to UpperBound IntRes1[i] := IntRes1[i] OR (BoolRes.word[i].bit[j] AND BoolRes.word[i].bit[j+1]) j += 2 ENDFOR ENDFOR 2: // equal each IntRes1 := 0 FOR i := 0 to UpperBound IntRes1[i] := BoolRes.word[i].bit[i] ENDFOR 3: // equal ordered IntRes1 := (imm8[0] ? 0xFF : 0xFFFF) FOR i := 0 to UpperBound k := i FOR j := 0 to UpperBound-i IntRes1[i] := IntRes1[i] AND BoolRes.word[k].bit[j] k := k+1 ENDFOR ENDFOR ESAC // optionally negate results bInvalid := 0 FOR i := 0 to UpperBound IF imm8[4] IF imm8[5] // only negate valid IF b[n+size-1:n] == 0 bInvalid := 1 FI IF bInvalid // invalid, don't negate IntRes2[i] := IntRes1[i] ELSE // valid, negate IntRes2[i] := -1 XOR IntRes1[i] FI ELSE // negate all IntRes2[i] := -1 XOR IntRes1[i] FI ELSE // don't negate IntRes2[i] := IntRes1[i] FI ENDFOR // output IF imm8[6] // byte / word mask FOR i := 0 to UpperBound j := i*size IF IntRes2[i] dst[j+size-1:j] := (imm8[0] ? 0xFF : 0xFFFF) ELSE dst[j+size-1:j] := 0 FI ENDFOR ELSE // bit mask dst[UpperBound:0] := IntRes2[UpperBound:0] dst[127:UpperBound+1] := 0 FI
